const payloadJson = $input.first().json.body.payload_json;
const bizType = $input.first().json.body.biz_type; // 如果你的字段名不同就改这里

const payload = JSON.parse(payloadJson);

const records_url_list = [];

for (const enterprise of (payload.enterpriseVOS ?? [])) {
  for (const record of (enterprise.records ?? [])) {
    const id = record.id;
    const titleUrl = record.titleUrl; // 如果是 title_url 就改成 record.title_url
    records_url_list.push({ id, titleUrl, bizType });
  }
}

return [{ json: { records_url_list } }];

